javascript:!function(){while(document.body.children.length>0)document.body.removeChild(document.body.children[0]);let e=document.createElement('iframe');e.src=window.location.href;e.style.width='100vw';e.style.height='100vh';e.style.border='none';e.addEventListener('load',()=>{Array.from(e.contentDocument.getElementsByTagName('a')).map(e=>{e.target==='_top'&&(e.target='_self')});let t=e.contentWindow.location.href;window.history.replaceState({},'',t);document.title=e.contentDocument.title;let n=t.match(/\/([0-9]+)$/)[0],o=n&&parseInt(n[1]);console.log('chapter',o)});window.addEventListener('load',()=>console.log('window load'));document.body.appendChild(e)}()