javascript:!function(){let t=window.location.hostname===''&&window.location.pathname.match(/\/chapter\/[0-9]+\.html$/),e=window.location.hostname==='www.hpmor.com'&&window.location.pathname.match(/\/chapter\/[0-9]+$/);if(!t&&!e){console.error('hpmor-annotations: Unrecognized site, aborting.');return}if(document.getElementById('hpmor-annotations-frame')||document.getElementById('hpmor-annotations-overlay')){console.error('hpmor-annotations: Bookmarklet already applied, aborting.');return}while(document.body.children.length>0)document.body.removeChild(document.body.children[0]);let o=document.createElement('iframe');o.id='hpmor-annotations-frame';o.src=window.location.href;o.style.width='100vw';o.style.height='100vh';o.style.border='none';let n=document.createElement('script');n.src=t?'../dist/annotate.js':'https://tryneus.github.io/hpmor-annotations/dist/annotate.js';o.addEventListener('load',()=>{let t=o.contentDocument,e=t.createElement('div');e.id='hpmor-annotations-notes';t.body.insertBefore(e,t.body.childNodes[0]);Array.from(t.getElementsByTagName('a')).map(t=>{t.target==='_top'&&(t.target='_self')});t.getElementById('nav-form-top').target='_self';window.history.replaceState({},'',o.contentWindow.location.href);document.title=t.title;annotate?annotate():(n.onload=()=>annotate())});document.body.appendChild(o);document.body.appendChild(n)}()